import React, { useRef, useEffect, useState } from 'react';
import styled from 'styled-components';
import { HeartOutlined, HeartFilled, ShareAltOutlined, MessageOutlined } from '@ant-design/icons';
import { likeContent, unlikeContent } from '../services/interactionService';

const PlayerContainer = styled.div`
  width: 100%;
  height: 100%;
  position: relative;
  background: #000;
`;

const VideoElement = styled.video`
  width: 100%;
  height: 100%;
  object-fit: contain;
`;

const Controls = styled.div`
  position: absolute;
  right: 20px;
  bottom: 120px;
  display: flex;
  flex-direction: column;
  align-items: center;
  z-index: 10;
`;

const ControlButton = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.2);
  display: flex;
  justify-content: center;
  align-items: center;
  margin-bottom: 15px;
  cursor: pointer;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }
`;

const InfoOverlay = styled.div`
  position: absolute;
  left: 20px;
  bottom: 20px;
  color: white;
  z-index: 10;
`;

const Username = styled.h3`
  margin: 0;
  font-size: 18px;
`;

const Description = styled.p`
  margin: 5px 0 0;
  font-size: 14px;
`;

const VideoPlayer = ({ content, isActive }) => {
  const videoRef = useRef(null);
  const [liked, setLiked] = useState(false);
  const [playing, setPlaying] = useState(false);

  useEffect(() => {
    if (isActive) {
      if (videoRef.current) {
        videoRef.current.play()
          .then(() => setPlaying(true))
          .catch(err => console.error('Failed to play video:', err));
      }
    } else {
      if (videoRef.current) {
        videoRef.current.pause();
        setPlaying(false);
      }
    }
  }, [isActive]);

  const handleVideoClick = () => {
    if (videoRef.current) {
      if (playing) {
        videoRef.current.pause();
        setPlaying(false);
      } else {
        videoRef.current.play()
          .then(() => setPlaying(true))
          .catch(err => console.error('Failed to play video:', err));
      }
    }
  };

  const handleLike = async () => {
    try {
      if (liked) {
        await unlikeContent(content.id);
      } else {
        await likeContent(content.id);
      }
      setLiked(!liked);
    } catch (error) {
      console.error('Failed to like/unlike content:', error);
    }
  };

  // 对于嵌入式内容，使用iframe
  if (content.type === 'embed') {
    return (
      <PlayerContainer>
        <iframe
          src={content.embedUrl}
          width="100%"
          height="100%"
          frameBorder="0"
          allow="autoplay; encrypted-media"
          allowFullScreen
          title={content.title}
        />
        <Controls>
          <ControlButton onClick={handleLike}>
            {liked ? <HeartFilled style={{ color: '#ff4d4f' }} /> : <HeartOutlined style={{ color: 'white' }} />}
          </ControlButton>
          <ControlButton>
            <MessageOutlined style={{ color: 'white' }} />
          </ControlButton>
          <ControlButton>
            <ShareAltOutlined style={{ color: 'white' }} />
          </ControlButton>
        </Controls>
        <InfoOverlay>
          <Username>@{content.author.username}</Username>
          <Description>{content.description}</Description>
        </InfoOverlay>
      </PlayerContainer>
    );
  }

  // 对于直接视频内容
  return (
    <PlayerContainer onClick={handleVideoClick}>
      <VideoElement
        ref={videoRef}
        src={content.contentUrl}
        poster={content.thumbnailUrl}
        loop
        playsInline
        preload="auto"
      />
      <Controls>
        <ControlButton onClick={(e) => { e.stopPropagation(); handleLike(); }}>
          {liked ? <HeartFilled style={{ color: '#ff4d4f' }} /> : <HeartOutlined style={{ color: 'white' }} />}
        </ControlButton>
        <ControlButton onClick={(e) => e.stopPropagation()}>
          <MessageOutlined style={{ color: 'white' }} />
        </ControlButton>
        <ControlButton onClick={(e) => e.stopPropagation()}>
          <ShareAltOutlined style={{ color: 'white' }} />
        </ControlButton>
      </Controls>
      <InfoOverlay>
        <Username>@{content.author.username}</Username>
        <Description>{content.description}</Description>
      </InfoOverlay>
    </PlayerContainer>
  );
};

export default VideoPlayer;
