import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import './VideoPlayer.css';

const VideoFeed = () => {
    // Áä∂ÊÄÅÂèòÈáè
    const [currentIndex, setCurrentIndex] = useState(0);
    const [isScrolling, setIsScrolling] = useState(false);
    const [videos, setVideos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Refs
    const videoContainerRef = useRef(null);
    const activeVideoRef = useRef(null);
    const touchStartY = useRef(0);
    const touchEndY = useRef(0);

    // Ê®°ÊãüËßÜÈ¢ëÊï∞ÊçÆ - Âú®APIËøûÊé•Ââç‰ΩøÁî®
    const sampleVideoData = [
        {
            id: 1,
            url: "https://cdn.plyr.io/static/demo/View_From_A_Blue_Moon_Trailer-576p.mp4",
            poster: "https://cdn.plyr.io/static/demo/View_From_A_Blue_Moon_Trailer-HD.jpg",
            username: "@Âàõ‰ΩúËÄÖÂ∞èÊòé",
            description: "ËøôÊòØ‰∏Ä‰∏™Ë∂ÖÁ∫ßÊúâË∂£ÁöÑËßÜÈ¢ë #ÁÉ≠Èó® #Êé®Ëçê",
            likes: "1.2‰∏á",
            comments: "1345",
            shares: "2.3‰∏á"
        },
        {
            id: 2,
            url: "https://cdn.plyr.io/static/demo/View_From_A_Blue_Moon_Trailer-576p.mp4",
            poster: "https://cdn.plyr.io/static/demo/View_From_A_Blue_Moon_Trailer-HD.jpg",
            username: "@ÊóÖË°åËææ‰∫∫",
            description: "Áæé‰∏ΩÁöÑÈ£éÊôØÔºå‰Ω†ÊÉ≥ÂéªÂêóÔºü #ÊóÖË°å #È£éÊôØ",
            likes: "8.7‰∏á",
            comments: "5678",
            shares: "1.6‰∏á"
        },
        {
            id: 3,
            url: "https://cdn.plyr.io/static/demo/View_From_A_Blue_Moon_Trailer-576p.mp4",
            poster: "https://cdn.plyr.io/static/demo/View_From_A_Blue_Moon_Trailer-HD.jpg",
            username: "@ÁæéÈ£üÂçö‰∏ª",
            description: "Ë∂ÖÁÆÄÂçïÁöÑÁæéÈ£üÊïôÁ®ãÔºå‰∏âÂàÜÈíüÂ≠¶‰ºö #ÁæéÈ£ü #ÊïôÁ®ã",
            likes: "3.4‰∏á",
            comments: "2389",
            shares: "9876"
        },
        {
            id: 4,
            url: "https://cdn.plyr.io/static/demo/View_From_A_Blue_Moon_Trailer-576p.mp4",
            poster: "https://cdn.plyr.io/static/demo/View_From_A_Blue_Moon_Trailer-HD.jpg",
            username: "@ËàûËπàÂ∞èÂ§©Êâç",
            description: "Â≠¶Ë∑≥ÊúÄÊñ∞ÊµÅË°åËàûËπà #ËàûËπà #ÊåëÊàò",
            likes: "67.8‰∏á",
            comments: "14356",
            shares: "25.7‰∏á"
        },
        {
            id: 5,
            url: "https://cdn.plyr.io/static/demo/View_From_A_Blue_Moon_Trailer-576p.mp4",
            poster: "https://cdn.plyr.io/static/demo/View_From_A_Blue_Moon_Trailer-HD.jpg",
            username: "@ÁîüÊ¥ªÂ∞èÊäÄÂ∑ß",
            description: "‰∏ÄÂàÜÈíüËß£ÂÜ≥‰Ω†ÁöÑÊó•Â∏∏ÈöæÈ¢ò #ÊäÄÂ∑ß #ÁîüÊ¥ª",
            likes: "2.3‰∏á",
            comments: "4576",
            shares: "1.2‰∏á"
        }
    ];

    // Ê†ºÂºèÂåñÊï∞Â≠ó
    const formatNumber = (num) => {
        if (num >= 10000) {
            return (num / 10000).toFixed(1) + '‰∏á';
        }
        if (num >= 1000) {
            return (num / 1000).toFixed(1) + 'k';
        }
        return num.toString();
    };

    // APIÊï∞ÊçÆËé∑Âèñ
    useEffect(() => {
        const fetchVideos = async () => {
            try {
                setLoading(true);
                // Â∞ùËØï‰ªéAPIËé∑ÂèñÊï∞ÊçÆ
                // Ê≥®ÊÑè: Â¶ÇÊûúAPIËøòÊú™ÂÆûÁé∞ÔºåÂ∞ÜÊçïËé∑ÈîôËØØÂπ∂‰ΩøÁî®Á§∫‰æãÊï∞ÊçÆ
                const response = await axios.get('/api/content/recommended');

                // Ê†ºÂºèÂåñAPIËøîÂõûÁöÑÊï∞ÊçÆ
                const formattedVideos = response.data.map(video => ({
                    id: video.id,
                    url: video.contentUrl || video.embedUrl,
                    poster: video.thumbnailUrl,
                    username: video.author?.username || 'ÂåøÂêçÁî®Êà∑',
                    description: video.description || '',
                    likes: formatNumber(video.stats?.likes || 0),
                    comments: formatNumber(video.stats?.comments || 0),
                    shares: formatNumber(video.stats?.shares || 0)
                }));

                setVideos(formattedVideos);
            } catch (err) {
                console.error('Ëé∑ÂèñËßÜÈ¢ëÂ§±Ë¥•:', err);
                setError('Êó†Ê≥ïÂä†ËΩΩËßÜÈ¢ëÔºå‰ΩøÁî®Á§∫‰æãÊï∞ÊçÆ');
                // ‰ΩøÁî®Á§∫‰æãÊï∞ÊçÆ‰Ωú‰∏∫Â§áÁî®
                setVideos(sampleVideoData);
            } finally {
                setLoading(false);
            }
        };

        fetchVideos();
    }, []);

    // Â§ÑÁêÜËßÜÈ¢ëÊí≠Êîæ/ÊöÇÂÅú
    const toggleVideoPlayback = (videoElement, videoWrapper) => {
        if (!videoElement) return;

        if (videoElement.paused) {
            videoElement.play()
                .then(() => {
                    videoWrapper.classList.remove('paused');
                })
                .catch(err => {
                    console.error('Êí≠ÊîæËßÜÈ¢ëÂ§±Ë¥•:', err);
                    videoWrapper.classList.add('paused');
                });
        } else {
            videoElement.pause();
            videoWrapper.classList.add('paused');
        }
    };

    // ËÆæÁΩÆÂΩìÂâçËßÜÈ¢ëÁöÑÂºïÁî®
    const setVideoRef = useCallback(node => {
        if (node) {
            activeVideoRef.current = node;
            // Â∞ùËØïËá™Âä®Êí≠ÊîæÂΩìÂâçËßÜÈ¢ë
            node.play().catch(err => {
                console.log('Ëá™Âä®Êí≠ÊîæË¢´ÈòªÊ≠¢ÔºåÈúÄË¶ÅÁî®Êà∑‰∫§‰∫í:', err);
                node.parentNode.classList.add('paused');
            });
        }
    }, []);

    // Â§ÑÁêÜËøõÂ∫¶Êù°ÁÇπÂáª
    const handleProgressClick = (e, videoElement, progressContainer) => {
        e.stopPropagation(); // Èò≤Ê≠¢Ëß¶ÂèëËßÜÈ¢ëÊí≠Êîæ/ÊöÇÂÅú

        if (!videoElement || !progressContainer) return;

        const rect = progressContainer.getBoundingClientRect();
        const pos = (e.clientX - rect.left) / rect.width;
        videoElement.currentTime = pos * videoElement.duration;
    };

    // ÊªöÂä®Âà∞‰∏ã‰∏Ä‰∏™ËßÜÈ¢ë
    const scrollToNextVideo = () => {
        if (currentIndex >= videos.length - 1 || isScrolling) return;

        setIsScrolling(true);

        // ÊöÇÂÅúÂΩìÂâçËßÜÈ¢ë
        if (activeVideoRef.current) {
            activeVideoRef.current.pause();
        }

        setCurrentIndex(prevIndex => prevIndex + 1);

        // ÊªöÂä®ÂÆåÊàêÂêé
        setTimeout(() => {
            setIsScrolling(false);
        }, 300);
    };

    // ÊªöÂä®Âà∞‰∏ä‰∏Ä‰∏™ËßÜÈ¢ë
    const scrollToPrevVideo = () => {
        if (currentIndex <= 0 || isScrolling) return;

        setIsScrolling(true);

        // ÊöÇÂÅúÂΩìÂâçËßÜÈ¢ë
        if (activeVideoRef.current) {
            activeVideoRef.current.pause();
        }

        setCurrentIndex(prevIndex => prevIndex - 1);

        // ÊªöÂä®ÂÆåÊàêÂêé
        setTimeout(() => {
            setIsScrolling(false);
        }, 300);
    };

    // Â§ÑÁêÜËß¶Êë∏‰∫ã‰ª∂
    const handleTouchStart = (e) => {
        touchStartY.current = e.touches[0].clientY;
    };

    const handleTouchMove = (e) => {
        e.preventDefault(); // Èò≤Ê≠¢È°µÈù¢ÊªöÂä®
    };

    const handleTouchEnd = (e) => {
        touchEndY.current = e.changedTouches[0].clientY;
        const diffY = touchEndY.current - touchStartY.current;

        // ‰∏äÊªëÊü•Áúã‰∏ã‰∏Ä‰∏™ËßÜÈ¢ë
        if (diffY < -50) {
            scrollToNextVideo();
        }
        // ‰∏ãÊªëÊü•Áúã‰∏ä‰∏Ä‰∏™ËßÜÈ¢ë
        else if (diffY > 50) {
            scrollToPrevVideo();
        }
    };

    // Â§ÑÁêÜÈº†Ê†áÊªöËΩÆ‰∫ã‰ª∂
    const handleWheel = (e) => {
        e.preventDefault();

        if (e.deltaY > 0) {
            scrollToNextVideo();
        } else {
            scrollToPrevVideo();
        }
    };

    // ËÆæÁΩÆ‰∫ã‰ª∂ÁõëÂê¨Âô®
    useEffect(() => {
        const container = videoContainerRef.current;

        if (container) {
            container.addEventListener('wheel', handleWheel);
            container.addEventListener('touchstart', handleTouchStart);
            container.addEventListener('touchmove', handleTouchMove);
            container.addEventListener('touchend', handleTouchEnd);

            // Èò≤Ê≠¢È°µÈù¢ÊªöÂä®Âπ≤Êâ∞ËßÜÈ¢ëÊªëÂä®
            document.body.style.overflow = 'hidden';
        }

        return () => {
            if (container) {
                container.removeEventListener('wheel', handleWheel);
                container.removeEventListener('touchstart', handleTouchStart);
                container.removeEventListener('touchmove', handleTouchMove);
                container.removeEventListener('touchend', handleTouchEnd);
            }

            // ÊÅ¢Â§çÈ°µÈù¢ÊªöÂä®
            document.body.style.overflow = 'auto';
        };
    }, []);

    // Êõ¥Êñ∞ËßÜÈ¢ëËøõÂ∫¶Êù°
    useEffect(() => {
        // Ëé∑ÂèñÂΩìÂâçÊøÄÊ¥ªÁöÑËßÜÈ¢ëÂÖÉÁ¥†
        const videoElement = activeVideoRef.current;

        if (!videoElement) return;

        // ËøõÂ∫¶Êù°Êõ¥Êñ∞ÂáΩÊï∞
        const updateProgress = () => {
            const progressBar = videoElement.parentNode.querySelector('.progress-bar');
            if (progressBar && videoElement.duration) {
                const percent = (videoElement.currentTime / videoElement.duration) * 100;
                progressBar.style.width = `${percent}%`;
            }
        };

        // Ê∑ªÂä†‰∫ã‰ª∂ÁõëÂê¨Âô®
        videoElement.addEventListener('timeupdate', updateProgress);

        // Ê∏ÖÁêÜÂáΩÊï∞
        return () => {
            videoElement.removeEventListener('timeupdate', updateProgress);
        };
    }, [currentIndex]);

    // Âä†ËΩΩ‰∏≠Áä∂ÊÄÅ
    if (loading) {
        return (
            <div className="video-container loading">
                <div className="loading-spinner"></div>
                <div style={{ color: 'white', textAlign: 'center', marginTop: '20px' }}>Âä†ËΩΩ‰∏≠...</div>
            </div>
        );
    }

    // ÈîôËØØÁä∂ÊÄÅ
    if (error && videos.length === 0) {
        return (
            <div className="video-container error">
                <div style={{ color: 'white', textAlign: 'center' }}>
                    <h3>Âä†ËΩΩÂ§±Ë¥•</h3>
                    <p>{error}</p>
                    <button onClick={() => window.location.reload()}>ÈáçËØï</button>
                </div>
            </div>
        );
    }

    // Ê≤°ÊúâËßÜÈ¢ë
    if (videos.length === 0) {
        return (
            <div className="video-container empty">
                <div style={{ color: 'white', textAlign: 'center' }}>
                    <h3>Ê≤°ÊúâÂèØÁî®ÁöÑËßÜÈ¢ë</h3>
                    <p>ËØ∑Á®çÂêéÂÜçËØï</p>
                </div>
            </div>
        );
    }

    // Ê∏≤ÊüìËßÜÈ¢ëÈ°π
    const renderVideoItem = (video, position) => {
        if (!video) return null;

        const isActive = position === 'active';

        return (
            <div key={video.id} className={`video-item ${position}`} data-video-id={video.id}>
                <div
                    className={`video-wrapper ${isActive ? '' : 'paused'}`}
                    onClick={(e) => {
                        const videoElement = e.currentTarget.querySelector('video');
                        toggleVideoPlayback(videoElement, e.currentTarget);
                    }}
                >
                    <div className="loading-spinner"></div>
                    <video
                        ref={isActive ? setVideoRef : null}
                        src={video.url}
                        poster={video.poster}
                        playsInline
                        webkit-playsinline="true"
                        muted={!isActive}
                        loop
                        preload="auto"
                        onCanPlay={(e) => {
                            const spinner = e.target.parentNode.querySelector('.loading-spinner');
                            if (spinner) {
                                spinner.style.display = 'none';
                            }
                        }}
                        onError={(e) => {
                            console.error('ËßÜÈ¢ëÂä†ËΩΩÂ§±Ë¥•:', video.url);
                            const spinner = e.target.parentNode.querySelector('.loading-spinner');
                            if (spinner) {
                                spinner.style.display = 'none';
                            }
                            // ÊòæÁ§∫ÈîôËØØÊèêÁ§∫
                            const errorMsg = document.createElement('div');
                            errorMsg.style = 'position:absolute; top:50%; left:50%; transform:translate(-50%,-50%); background:rgba(0,0,0,0.7); padding:10px 20px; border-radius:5px; color:white;';
                            errorMsg.innerText = 'ËßÜÈ¢ëÂä†ËΩΩÂ§±Ë¥•';
                            e.target.parentNode.appendChild(errorMsg);
                        }}
                    >
                    </video>
                    <div className="play-button">
                        <svg width="24" height="24" viewBox="0 0 24 24" fill="white">
                            <path d="M8 5v14l11-7z"></path>
                        </svg>
                    </div>
                    <div className="video-controls">
                        <div
                            className="progress-bar-container"
                            onClick={(e) => {
                                const videoElement = e.currentTarget.parentNode.parentNode.querySelector('video');
                                handleProgressClick(e, videoElement, e.currentTarget);
                            }}
                        >
                            <div className="progress-bar"></div>
                        </div>
                    </div>
                    <div className="video-info">
                        <h3>{video.username}</h3>
                        <p>{video.description}</p>
                    </div>
                    <div className="video-actions">
                        <div className="action-btn">
                            <svg viewBox="0 0 24 24"><path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z" /></svg>
                            <div className="action-count">{video.likes}</div>
                        </div>
                        <div className="action-btn">
                            <svg viewBox="0 0 24 24"><path d="M21.99 4c0-1.1-.89-2-1.99-2H4c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h14l4 4-.01-18z" /></svg>
                            <div className="action-count">{video.comments}</div>
                        </div>
                        <div className="action-btn">
                            <svg viewBox="0 0 24 24"><path d="M18 16.08c-.76 0-1.44.3-1.96.77L8.91 12.7c.05-.23.09-.46.09-.7s-.04-.47-.09-.7l7.05-4.11c.54.5 1.25.81 2.04.81 1.66 0 3-1.34 3-3s-1.34-3-3-3-3 1.34-3 3c0 .24.04.47.09.7L8.04 9.81C7.5 9.31 6.79 9 6 9c-1.66 0-3 1.34-3 3s1.34 3 3 3c.79 0 1.5-.31 2.04-.81l7.12 4.16c-.05.21-.08.43-.08.65 0 1.61 1.31 2.92 2.92 2.92 1.61 0 2.92-1.31 2.92-2.92s-1.31-2.92-2.92-2.92z" /></svg>
                            <div className="action-count">{video.shares}</div>
                        </div>
                    </div>
                </div>
            </div>
        );
    };

    return (
        <>
            <div className="video-container" ref={videoContainerRef}>
                {/* Ê∏≤ÊüìÂâç‰∏Ä‰∏™ËßÜÈ¢ëÔºàÂ¶ÇÊûúÂ≠òÂú®Ôºâ */}
                {currentIndex > 0 && renderVideoItem(videos[currentIndex - 1], 'prev')}

                {/* Ê∏≤ÊüìÂΩìÂâçËßÜÈ¢ë */}
                {renderVideoItem(videos[currentIndex], 'active')}

                {/* Ê∏≤Êüì‰∏ã‰∏Ä‰∏™ËßÜÈ¢ëÔºàÂ¶ÇÊûúÂ≠òÂú®Ôºâ */}
                {currentIndex < videos.length - 1 && renderVideoItem(videos[currentIndex + 1], 'next')}
            </div>

            <div className="bottom-nav">
