import React, { useState, useEffect } from 'react';
import { Layout } from 'antd';
import styled from 'styled-components';
import SearchBar from '../components/SearchBar';
import VideoFeed from '../components/VideoFeed';
import ChatSection from '../components/ChatSection';
import ActionPanel from '../components/ActionPanel';
import { fetchRecommendedContent } from '../services/contentService';
import { useAuth } from '../contexts/AuthContext';

const { Header, Sider, Content } = Layout;

const StyledLayout = styled(Layout)`
  height: 100vh;
  background: #121212;
`;

const StyledHeader = styled(Header)`
  background: #202020;
  padding: 0 20px;
  display: flex;
  align-items: center;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
`;

const LeftSider = styled(Sider)`
  background: #202020;
  overflow: auto;
`;

const RightSider = styled(Sider)`
  background: #202020;
`;

const MainContent = styled(Content)`
  background: #121212;
  position: relative;
`;

const MainFeed = () => {
    const [contents, setContents] = useState([]);
    const [currentContent, setCurrentContent] = useState(null);
    const [loading, setLoading] = useState(true);
    const { user } = useAuth();

    useEffect(() => {
        const loadContent = async () => {
            try {
                const data = await fetchRecommendedContent(user?.id);
                setContents(data);
                if (data.length > 0) {
                    setCurrentContent(data[0]);
                }
                setLoading(false);
            } catch (error) {
                console.error('Failed to load content:', error);
                setLoading(false);
            }
        };

        loadContent();
    }, [user]);

    const handleContentChange = (content) => {
        setCurrentContent(content);
    };

    return (
        <StyledLayout>
            <StyledHeader>
                <SearchBar />
            </StyledHeader>
            <Layout>
                <LeftSider width={300}>
                    <ChatSection contentId={currentContent?.id} />
                </LeftSider>
                <MainContent>
                    <VideoFeed
                        contents={contents}
                        currentContent={currentContent}
                        onContentChange={handleContentChange}
                        loading={loading}
                    />
                </MainContent>
                <RightSider width={300}>
                    <ActionPanel content={currentContent} />
                </RightSider>
            </Layout>
        </StyledLayout>
    );
};

export default MainFeed;
